import React from 'react';
import HTMLReactParser from 'html-react-parser';
import { Table, Typography, Col, Image, Avatar } from 'antd';
import millify from 'millify';
import { useGetCryptoExchangesQuery } from '../services/cryptoApi';

const { Title, Text } = Typography;


const Exchanges = () => {
    const { data, isFetching } = useGetCryptoExchangesQuery(10);
    const exchangesData = data?.data?.exchanges

    if (isFetching) return 'Loading...'


    console.log(exchangesData)

    const columns = [
        {
            title: 'Exchanges',
            dataIndex: ['name', 'iconUrl'],
            render: (text, record) => <Text strong>{record.rank}.<Avatar className="exchange-image" src={record.iconUrl} />{record.name}</Text>
        },
        { title: '24h Trade Volume', dataIndex: 'volume', render: text => `$${millify(text)}` },
        { title: 'Markets', dataIndex: 'numberOfMarkets', render: markets => millify(markets) },
        { title: 'Change', dataIndex: 'marketShare', render: share => `${millify(share)}%` },
    ];


    return (
        <Table 
            columns={columns}
            expandable={{
                expandedRowRender: record => <p style={{ margin: 0 }}>{HTMLReactParser(record.description)}</p>,
                rowExpandable: record => record.description !== {Null},
              }}
            dataSource={exchangesData} />
    )
}

export default Exchanges
